mapper.select:
SELECT 
FROM t_content_chapter

    
        
        AND content_id = #{contentId}
        
    
    
        
        AND status = #{status}
        
    
    
        
        AND chapter_num >= #{chapterNumStart}
        
    
    
        
        AND chapter_num <= #{chapterNumEnd}
        
    
    
        
        AND word_count >= #{minWordCount}
        
    
    
        
        AND word_count <= #{maxWordCount}
        
    


    
        ORDER BY chapter_num DESC
    
    
        ORDER BY chapter_num ASC
    
    
        ORDER BY create_time ASC
    
    
        ORDER BY create_time DESC
    
    
        ORDER BY update_time ASC
    
    
        ORDER BY update_time DESC
    
    
        ORDER BY word_count ASC
    
    
        ORDER BY word_count DESC
    
    
        ORDER BY chapter_num ASC
    


    LIMIT #{currentPage}, #{pageSize}

SELECT 
FROM t_content_chapter

    
        
        AND (LOWER(title) LIKE CONCAT('%', LOWER(#{keyword}), '%')
        OR LOWER(content) LIKE CONCAT('%', LOWER(#{keyword}), '%'))
        
    
    
        
        AND content_id = #{contentId}
        
    
    
        
        AND status = #{status}
        
    


ORDER BY create_time DESC


    
    LIMIT #{currentPage}, #{pageSize}

mapper.update:
UPDATE t_content_chapter

SET status = #{status}
WHERE id = #{id}

UPDATE t_content_chapter

SET title = #{title}, 
    content = #{content}, 
    word_count = #{wordCount}
WHERE id = #{id}

UPDATE t_content_chapter

SET status = #{status}
WHERE id IN


    #{id}

UPDATE t_content_chapter

SET status = 'DELETED'
WHERE id = #{id}

UPDATE t_content_chapter
SET status = 'DELETED' WHERE id IN
#{id}
