mapper.select:
SELECT FROM t_hot_search WHERE status = 'active'
AND search_count > 0 AND trend_score > 0 ORDER BY (search_count * 0.6 + trend_score * 0.4) DESC, update_time DESC
LIMIT #{limit}

SELECT FROM t_hot_search WHERE status = 'active'
AND trend_score > 1.0 ORDER BY trend_score DESC, search_count DESC
LIMIT #{limit}

SELECT hs. FROM t_hot_search hs INNER JOIN ( SELECT keyword, COUNT(*) as recent_count FROM t_search_history
WHERE create_time >= DATE_SUB(NOW(), INTERVAL 1 HOUR) GROUP BY keyword HAVING COUNT(*) >= #{minSurgeCount}
) recent ON hs.keyword = recent.keyword WHERE hs.status = 'active' ORDER BY recent.recent_count DESC, hs.search_count DESC
LIMIT #{limit}

SELECT 
FROM t_hot_search
WHERE status = 'inactive'

AND update_time < #{beforeTime}
ORDER BY update_time ASC


    
    LIMIT #{limit}
