mapper.select:
SELECT 
FROM t_search_history
WHERE user_id = #{userId}
ORDER BY create_time DESC

    
    LIMIT #{limit}

SELECT 
FROM t_search_history
WHERE user_id = #{userId}
AND search_type = #{searchType}
ORDER BY create_time DESC

    
    LIMIT #{limit}

SELECT COUNT(*)
FROM t_search_history
WHERE user_id = #{userId}
AND keyword = #{keyword}

SELECT search_type, COUNT(*) as search_count, COUNT(DISTINCT keyword) as unique_keywords, AVG(result_count) as avg_result_count FROM t_search_history WHERE user_id = #{userId}
AND create_time >= #{startTime}
AND create_time <= #{endTime}
GROUP BY search_type ORDER BY search_count DESC

SELECT HOUR(create_time) as search_hour, COUNT(*) as search_count FROM t_search_history WHERE user_id = #{userId}
AND create_time >= DATE_SUB(NOW(), INTERVAL #{days} DAY)
GROUP BY HOUR(create_time) ORDER BY search_hour ASC

SELECT DATE(create_time) as search_date, COUNT(*) as total_searches, COUNT(DISTINCT user_id) as unique_users, COUNT(DISTINCT keyword) as unique_keywords, AVG(result_count) as avg_result_count FROM t_search_history WHERE 1=1
AND search_type = #{searchType}
AND create_time >= #{startTime}
AND create_time <= #{endTime}
GROUP BY DATE(create_time) ORDER BY search_date DESC

SELECT sh2.user_id, COUNT(DISTINCT sh1.keyword) as common_keywords, COUNT(DISTINCT sh2.keyword) as total_keywords, ROUND(COUNT(DISTINCT sh1.keyword) * 100.0 / COUNT(DISTINCT sh2.keyword), 2) as similarity_score FROM t_search_history sh1 INNER JOIN t_search_history sh2 ON sh1.keyword = sh2.keyword WHERE sh1.user_id = #{userId} AND sh2.user_id != #{userId}
AND sh1.create_time >= DATE_SUB(NOW(), INTERVAL #{days} DAY) AND sh2.create_time >= DATE_SUB(NOW(), INTERVAL #{days} DAY)
GROUP BY sh2.user_id
HAVING common_keywords >= #{minCommonKeywords}
ORDER BY similarity_score DESC, common_keywords DESC
LIMIT #{limit}

SELECT keyword, search_type, COUNT(*) as search_frequency, AVG(result_count) as avg_results, MIN(result_count) as min_results, MAX(result_count) as max_results, CASE WHEN AVG(result_count) = 0 THEN 'no_results'
WHEN AVG(result_count) < 5 THEN 'low_results' WHEN AVG(result_count) < 20 THEN 'medium_results'
ELSE 'high_results' END as effectiveness_level FROM t_search_history WHERE 1=1
AND user_id = #{userId}
AND search_type = #{searchType}
AND create_time >= #{startTime}
AND create_time <= #{endTime}
GROUP BY keyword, search_type ORDER BY search_frequency DESC, avg_results DESC

SELECT 
FROM t_search_history
WHERE result_count = 0

    AND search_type = #{searchType}


    
    AND create_time >= #{startTime}
    


    
    AND create_time <= #{endTime}
    

ORDER BY create_time DESC

    
    LIMIT #{limit}

mapper.delete:
DELETE FROM t_search_history

WHERE create_time < DATE_SUB(NOW(), INTERVAL #{days} DAY)


    
    LIMIT #{limit}

mapper.select:
SELECT COUNT(*)
FROM t_search_history
WHERE 1=1

    AND user_id = #{userId}


    AND search_type = #{searchType}


    
    AND create_time >= #{startTime}
    


    
    AND create_time <= #{endTime}
