mapper.update:
UPDATE t_user_interest_tag 
SET interest_score = #{interestScore}
WHERE user_id = #{userId} AND tag_id = #{tagId}

UPDATE t_user_interest_tag 
SET status = #{status}
WHERE user_id = #{userId} 
AND tag_id IN

    #{tagId}

mapper.select:
SELECT tag_id, interest_score, status, create_time FROM t_user_interest_tag WHERE user_id = #{userId} AND status = 'active'
AND interest_score >= #{minScore}
ORDER BY interest_score DESC, create_time DESC

SELECT uit.*, t.name as tag_name, t.description as tag_description FROM t_user_interest_tag uit LEFT JOIN t_tag t ON uit.tag_id = t.id WHERE 1=1
AND uit.user_id = #{userId}
AND uit.tag_id = #{tagId}
AND uit.interest_score >= #{minInterestLevel}
AND uit.interest_score <= #{maxInterestLevel}
AND uit.status = #{status}
AND (t.name LIKE CONCAT('%', #{keyword}, '%') OR t.description LIKE CONCAT('%', #{keyword}, '%'))
ORDER BY uit.interest_score ASC
ORDER BY uit.interest_score DESC
ORDER BY uit.create_time ASC
ORDER BY uit.create_time DESC
ORDER BY uit.update_time ASC
ORDER BY uit.update_time DESC
ORDER BY t.name ASC
ORDER BY t.name DESC
ORDER BY uit.interest_score DESC
